mapper.select:
        SELECT


--         级联表的字段
        ,jijin.jijin_uuid_number as jijinUuidNumber
        ,jijin.jijin_name as jijinName
        ,jijin.jijin_photo as jijinPhoto
        ,jijin.jijin_types as jijinTypes
        ,jijin.jijin_clicknum as jijinClicknum
        ,jijin.jijin_tuijian_types as jijinTuijianTypes
        ,jijin.jijin_content as jijinContent
        ,jijin.shangxia_types as shangxiaTypes
        ,jijin.jijin_delete as jijinDelete
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney

        FROM jiaoyijilu  a
        left JOIN jijin jijin ON a.jijin_id = jijin.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.jijin_id = #{params.jijinId}


                and a.yonghu_id = #{params.yonghuId}


                  and a.jiaoyijilu_fenshu >= #{params.jiaoyijiluFenshuStart}   


                  and a.jiaoyijilu_fenshu <= #{params.jiaoyijiluFenshuEnd}   


                and a.jiaoyijilu_fenshu = #{params.jiaoyijiluFenshu}


                  and a.jiaoyijilu_sum_money >= #{params.jiaoyijiluSumMoneyStart}   


                  and a.jiaoyijilu_sum_money <= #{params.jiaoyijiluSumMoneyEnd}   


                and a.jiaoyijilu_types = #{params.jiaoyijiluTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



                and jijin.jijin_uuid_number like CONCAT('%',#{params.jijinUuidNumber},'%')


                and jijin.jijin_name like CONCAT('%',#{params.jijinName},'%')


                and jijin.jijin_types = #{params.jijinTypes}



                  and jijin.jijin_clicknum >= #{params.jijinClicknumStart}   


                  and jijin.jijin_clicknum <= #{params.jijinClicknumEnd}   


                and jijin.jijin_clicknum = #{params.jijinClicknum}


                and jijin.jijin_tuijian_types = #{params.jijinTuijianTypes}



                and jijin.jijin_content like CONCAT('%',#{params.jijinContent},'%')


                and jijin.shangxia_types = #{params.shangxiaTypes}



                  and jijin.jijin_delete >= #{params.jijinDeleteStart}   


                  and jijin.jijin_delete <= #{params.jijinDeleteEnd}   


                and jijin.jijin_delete = #{params.jijinDelete}


                  and UNIX_TIMESTAMP(jijin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(jijin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   



        order by a.${params.orderBy} desc
